css:
/*Fonte*/
body{
  font-family:'Georama';
}

/*Diminuir_Barra_Marketing*/

.top-bar__countdown {
    font-size: 0.8em!important;
    padding-top: 5px!important;
    padding-bottom: 5px!important;
}

/*SOMBRA LISTAGEM DE PRODUTOS*/
article.product-item {
    box-shadow: 0 0 1em #0b66665c;
    margin: 10px;
}

/*REMOVER FOTO DO PRODUT NA LISTAGEM*/
.product-card__pictures {
    display: none;
}

/* mini banners mobile*/
@media(max-width: 767px) {
  .page--home .banners-grid .col-lg-3 {
    max-width: 50% !important;
    padding-right: 10px;
    padding-left: 10px;
  }
}

/* fundo para lista de produtos*/
.search-engine__item {
    margin-bottom: 10px;
    padding-bottom: 28px;
    box-shadow: 0 0 1.1em #f5f5f5;
}

/*Fazer o Botão aparecer*/
.product-card__buy.fade {
    opacity: 1!important;
    padding-top: 10px;
}

/*Diminuir_font_barra-pages-topo*/
.top-bar__page-links a {
    font-weight: 500;
    font-size: 0.8em;
}

/*Alinhar_texto_barra-pages-topo*/
.top-bar__nav {
    text-align: end!important;
}

/*cor_infobar*/
.info-bar {
    width: 80%;
    max-width: 60rem;
    margin-left: auto;
    margin-right: auto;
    background: #ffffff00;
    color: #0b6666;
}

/*Tamanho_font_infobar*/

.info-bar__item {
    text-transform: uppercase;
    font-weight: 500!important;
    font-size: 0.8em!important;
}

/*Sombra_Topo_*/
#header {
    box-shadow: 0 1.1rem 1rem rgba(0,0,0,.15)!important;
    border-width: 0;
    padding-top: 28px;
    padding-left: 0;
    padding-right: 0;
}


/*Retirar Imagem Produto_*/
.col-12.col-md-6.mb-4{
	/*display: none;*/
}

/*Centralizar "Produto" na pagina*/
.col{
	text-align: center;
}


/*Remover formas de pagamento*/
.product__payment.card.mb-3{
	display: none;
}

/*Remover texto descrição*/
.lead {
    display: none;
}

/*Remover SKU produto*/
.product__sku{
    display: none;
}

/*Centralizar Campo CEP*/
.input-group {
    justify-content: center;
}

/*Espaçamento entre elementos menu*/
.header__nav {
     display: flex;
     justify-content: center; 
     line-height: 15px
}

/*Edições menu*/
.header__row .header__nav a {
    position: relative;
    font-weight: 500!important;
    padding: 10px 10px;
    text-transform: none;
    font-size: 15px;
    margin: 0;
}

/*Menu_barra_*/
.header__row .header__nav a:after {
    background: #3CB5B3;
    position: absolute;
    content: "";
    bottom: 0;
    left: 0;
    width: 100%;
    height: 3px;
    opacity: 0;
    transition: .2s cubic-bezier(.4,0,.22,1);
}

/*Mostrar traço hover menu*/
.header__row .header__nav a:hover:after {
    opacity: 1;
}

.header__nav a:hover {
    background: transparent;
}

/*Barra de Pesquisa Arredondada*/

.header__search-input {
    padding: 0 var(--spacer-3);
    border-radius: 50px!importante;
    font-size: 0.9em!important;
    line-height: var(--line-height-lg);
    height: 45px;
}

/*Lupa_tamanho e Local)*/
/*Tamanho*/
.header__search-btn {
    font-size: 1.3em;
    margin-bottom: calc(var(--spacer-2)*-1 + -0.70rem);
}
}
/*local*/
.header__search-btn {
    border: none;
    background: none;
    position: absolute;
    bottom: 50%;
    font-size: 1.3rem;
    line-height: 1;
    margin-bottom: calc(var(--spacer-2)*-1 + -.65rem);
    right: var(--spacer-2);
    padding: var(--spacer-2);
    color: var(--secondary-lighter);
    transition: color .125s;
}

/*Menu-borda-redonda*/
.header__nav a {
        border-radius: 25px;
}

/*Borda campo pesquisar*/
.header__search {
    border-radius: 2rem;
}

/*Tirar_Barra_Top*/
@media only screen and (min-width: 768px) {
.top-bar__nav {display: none !important;}
}
.top-bar__nav {
    padding: 0;
}

/*Fixar Barra Marketing Topo*/
.top-bar {
    position: sticky;
    top: 0px !important;
    z-index: 500;
}

.header {
    position: sticky;
    top: 29px;
    width: 100%;
    background: var(--light);
    padding: var(--spacer-1);
    border-bottom: 1px solid var(--primary-lighter);
    box-shadow: var(--box-shadow-sm);
    z-index: 900;
}

/*Centralizar Lista de Coletas*/
.shipping-calculator__services {
    margin: auto;
}

/*Alteração Tamanho Logo*/
/*Desktop*/
@media (min-width: 576px) {
  .header__logo {
    max-width: 220px;
    max-height: 60px;
  }
}  
/*Mobile*/
@media (max-width: 576px) {
  .header__logo {
    max-width: 195px;
    max-height: 60px;
  }
}



/*MUDANÇA DE CORES*/

/*Botões*/
@media  (hover: hover) {
  .header__buttons .btn:hover {
      color: #3CB5B3;
  }
}

/*Botão Pesquisar*/
.header__search-btn {
    color: #bbe1d9;
}

@media (hover: hover) {
  .header__search-btn:hover {
    color: #3CB5B3;
  }
}

/*Borda Menu Lateral*/
    .menu__item {
    border-bottom: var(--border-width) solid #bbe1d9;
}
/*Menu Lateral Hover*/
    .menu__item:hover {
    background: #3CB5B3;
}
/*Menu Lateral Icon Hover*/


/*Lights*/
:root {
--primary-lightest: #3CB5B3;
--secondary-lightest: #3CB5B3;
--primary-lighter: #3CB5B3;
--secondary-lighter: #3CB5B3;
    }

/*fim da mudanças de cores*/

/* Infobar distancia do slider */
  .info-bar__item {
    margin-top: var(--spacer-4);
}

/*Fullbanner*/
@media  screen and (min-width: 992px) {
  .banner-slider {
    max-width: 100% !important; height: auto; --banner-aspect-ratio: 1 !important;
  }
}

