css:
/*Fonte*/
body{
  font-family:'Georama';
}

/*Diminuir_Barra_Marketing*/

.top-bar__countdown {
    font-size: 0.8em!important;
    padding-top: 5px!important;
    padding-bottom: 5px!important;
}


/*Diminuir_font_barra-pages-topo*/
.top-bar__page-links a {
    font-weight: 500;
    font-size: 0.8em;
}

/*Alinhar_texto_barra-pages-topo*/
.top-bar__nav {
    text-align: end!important;
}

/*cor_infobar*/
.info-bar {
    width: 80%;
    max-width: 60rem;
    margin-left: auto;
    margin-right: auto;
    background: #ffffff00;
    color: #0b6666;
}

/*Tamanho_font_infobar*/
@media (min-width: 576px)
.info-bar__item {
    text-transform: uppercase;
    font-weight: 500!important;
    font-size: 0.8em!important;
}

/*Sombra_Topo_*/
#header {
    box-shadow: 0 .5rem 1rem rgba(0,0,0,.15)!important;
    border-width: 0;
    padding-top: 0;
    padding-left: 0;
    padding-right: 0;
}


/*Retirar Imagem Produto_*/
.col-12.col-md-6.mb-4{
	display: none;
}

/*Centralizar "Produto" na pagina*/
.col{
	text-align: center;
}

/*Remover bandeiras_*/
.mb-3{
	display: none;
}

/*Remover formas de pagamento*/
.product__payment.card.mb-3{
	display: none;
}

/*Remover texto descrição*/
.lead {
    display: none;
}

/*Remover SKU produto*/
.product__sku{
    display: none;
}

/*Centralizar Campo CEP*/
.input-group {
    justify-content: center;
}

/*Menu_barra_*/
.header__row .header__nav a:after {
    background: #0b6666;
    position: absolute;
    content: "";
    bottom: 0;
    left: 0;
    width: 100%;
    height: 3px;
    opacity: 0;
    transition: .2s cubic-bezier(.4,0,.22,1);
}

/*Barra de Pesquisa Arredondada*/
media (min-width: 768px) 
.header__search-input {
    padding: 0 var(--spacer-3);
    border-radius: 50px!importante;
    font-size: 0.9em!important;
    line-height: var(--line-height-lg);
    height: 45px;
}

/*Lupa_tamanho e Local)*/
/*Tamanho*/
@media (min-width: 768px) {
.header__search-btn {
    font-size: 1.3em;
    margin-bottom: calc(var(--spacer-2)*-1 + -0.70rem);
}
}
/*local*/
.header__search-btn {
    border: none;
    background: none;
    position: absolute;
    bottom: 50%;
    font-size: 1.3rem;
    line-height: 1;
    margin-bottom: calc(var(--spacer-2)*-1 + -.65rem);
    right: var(--spacer-2);
    padding: var(--spacer-2);
    color: var(--secondary-lighter);
    transition: color .125s;
}

/*Menu-borda-redonda*/
.header__nav a {
        border-radius: 25px;
}
